mapper.select:
SELECT 
	id e_id,
	category e_category,
	amount e_amount,
	entry_date e_date,
	created e_created,
	modified e_modified
FROM entries
WHERE 
	user_id = #{user.id}

	AND category = #{entry.categoryId}
	AND entry_date = #{entry.date}


	AND id = #{id}


	AND category = #{categoryId}


	AND entry_date = #{date}

mapper.update:
UPDATE entries
SET
	amount = #{entry.amount},
	modified = CURRENT_TIMESTAMP
WHERE
	user_id = #{user.id}
	AND category = #{entry.categoryId} 
	AND entry_date = #{entry.date}
