mapper.select:
SELECT 
	c.id s_id, c.user_id s_user_id, c.uuid s_uuid, c.name s_name, c.start_date s_start_date, c.deleted s_deleted, c.type s_type, c.created s_created, c.modified s_modified,
	c.period_type c_period_type, c.parent c_parent 
FROM sources c
WHERE user_id = #{user.id}
	AND id = #{id}

SELECT 
	c.id s_id, c.user_id s_user_id, c.uuid s_uuid, c.name s_name, c.start_date s_start_date, c.deleted s_deleted, c.type s_type, c.created s_created, c.modified s_modified,
	c.period_type c_period_type, c.parent c_parent

	, ce.id ce_id, pe.id pe_id, ce.amount ce_amount, pe.amount pe_amount, ce.date ce_date, pe.date pe_date

FROM sources c

	LEFT JOIN entries ce ON ce.category = c.id AND ce.date = #{categoryPeriod.currentDate} AND ce.user_id = #{user.id}
	LEFT JOIN entries pe ON pe.category = c.id AND pe.date = #{categoryPeriod.previousDate} AND pe.user_id = #{user.id}

WHERE c.user_id = #{user.id}
AND period_type = #{categoryPeriod.periodType}
AND period_type = #{periodType}


		AND type = 'I'
		AND type = 'E'


ORDER BY c.type DESC, c.name

SELECT * FROM sources 
WHERE user_id = #{user.id}

		and id = #{id}
