mapper.select:
SELECT t.id t_id, t.uuid, t.user_id t_user_id, t.description, t.number, t.date, t.deleted, t.created t_created, t.modified t_modified, s.id s_id, s.transaction_id, s.amount, s.user_id s_user_id, s.from_source, s.to_source, s.memo, s.created s_created, s.modified s_modified, from_balance, to_balance, fs.name from_source_name, ts.name to_source_name FROM transactions t LEFT JOIN splits s ON s.transaction_id = t.id INNER JOIN sources fs ON fs.id = s.from_source INNER JOIN sources ts ON ts.id = s.to_source WHERE t.user_id = #{user.id}
AND (s.from_source = #{source.id} OR s.to_source = #{source.id})
ORDER BY t.date, t.description, s.id

SELECT 
	t.id t_id, t.uuid, t.user_id t_user_id, t.description, t.number, t.date, t.deleted, t.created t_created, t.modified t_modified
FROM transactions t
WHERE t.user_id = #{user.id}
AND t.id = #{id}

mapper.insert:
SELECT coalesce(max(id), 0) + 1 from transactions
INSERT INTO transactions ( id, uuid, user_id, description, number, date, deleted, created, modified
) values (#{transaction.id}, #{transaction.uuid}, #{user.id}, #{transaction.description}, #{transaction.number,jdbcType=VARCHAR}, #{transaction.date}, 'Y''N', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP)
