mapper.select:
SELECT c.id s_id, c.user_id s_user_id, c.uuid s_uuid, c.name s_name, c.start_date s_start_date, c.deleted s_deleted, c.type s_type, c.created s_created, c.modified s_modified, c.period_type c_period_type, c.parent c_parent FROM sources c WHERE user_id = #{user.id}
AND (type = 'I' OR type = 'E') AND type = 'I' AND type = 'E'
ORDER BY c.type DESC, c.name

SELECT * FROM sources 
WHERE user_id = #{user.id}

		and id = #{id}
